mapper.update:
UPDATE COMMON_CODE SET CODE = #{code}, CODE_NAME = #{codeName},
USE_YN = #{useYn},
DESCRIPTION = #{description}, REF1 = #{ref1}, REF2 = #{ref2}, REF3 = #{ref3}, UPDATE_USER = #{updateUser}, UPDATE_DATE = SYSDATE, SORT = #{sort}
AND IDX = #{idx}

mapper.select:
SELECT  COUNT(*) AS CNT
FROM    COMMON_CODE
WHERE   CODE = #{code}
